import React, { Component } from "react";
import io from "socket.io-client";

class Header extends Component {
  constructor() {
    super();
    this.state = {
      endpoint: "http://localhost:3001/"
    };
    this.socket = io(this.state.endpoint);
  }
  render() {
    return (
      <div className="bg-gray-900 flex justify-center mb-32">
        <div className="text-white container mt-10" id="chat"></div>

        <form className="flex flex-col md:flex-row w-full h-64 md:h-24 bottom-0 container fixed">
          <input
            className="bg-transparent text-white w-full md:w-40 h-24 pl-2"
            type="text"
            name="name"
            placeholder="name"
            required
          />
          <input
            className="bg-transparent text-white w-full md:w-screen h-full md:h-24 pb-32 md:pb-0 pl-2"
            type="text"
            name="message"
            placeholder="message"
          />
          <button
            className="bg-purple-700 text-white p-3 rounded h-16"
            type="submit"
            name="send"
          >
            Send
          </button>
        </form>
      </div>
    );
  }
}
export default Header;
