import React, { Component } from "react";
import io from "socket.io-client";

var socket;
class Header extends Component {
  constructor() {
    super();
    this.state = {
      endpoint: "http://localhost:3001/",
      name: '',
      message: ''
    };
    socket = io(this.state.endpoint);

    this.handleNameChange = this.handleNameChange.bind(this)
    this.handleMessageChange = this.handleMessageChange.bind(this)
    this.handleSubmit = this.handleSubmit.bind(this)
  }

  handleNameChange(e){
    this.setState({
      name: e.target.value
    })
  }

  handleMessageChange(e){
    this.setState({
      message: e.target.value
    })
  }

  handleSubmit() {
    this.socket.emit('chat message', {name: this.state.name, message: this.state.message})
  }

  render() {
    return (
      <div className="flex justify-center mb-32">
        <div className="text-white container mt-10 bg-gray-900" id="chat"></div>
        <form className="flex flex-col md:flex-row w-full h-64 md:h-24 container fixed" onSubmit={this.handleSubmit}>
          <input
            className="bg-transparent w-full md:w-40 h-24 pl-2"
            type="text"
            placeholder="name"
            onChange={this.handleNameChange}
            value={this.state.name}
            required
          />
          <input
            className="bg-transparent w-full md:w-screen h-full md:h-24 pb-32 md:pb-0 pl-2"
            type="text"
            placeholder="message"
            value={this.state.message}
            onChange={this.handleMessageChange}
          />
          <button
            className="bg-purple-700 text-white p-3 rounded h-16"
            type="submit"
          >
            Send
          </button>
        </form>
      </div>
    );
  }
}
export default Header;
